import React from 'react';
import Select from 'react-select';
import ControlComponent from './styles/ControlComponent';
import { DropdownStyles } from './styles/dropdownStyles';
import DropdownWrapper from './styles';

const Dropdown = ({
  value,
  label,
  dropdownKey,
  stateKey = null,
  onChange,
  options,
  valid = true,
  padRight,
  className
}) => React.createElement(DropdownWrapper, {
  className: className,
  valid: valid || !valid && value === {},
  padRight: padRight
}, React.createElement(Select, {
  options: options,
  onChange: event => onChange(event.value, dropdownKey, stateKey),
  components: {
    Control: ControlComponent
  },
  name: "categoryOptions",
  classNamePrefix: dropdownKey === 'difficulty' ? 'expanded-select' : 'select',
  placeholder: label,
  styles: DropdownStyles,
  isClearable: false,
  isSearchable: false,
  value: value,
  instanceId: "dropdown-select"
}));

export default Dropdown;
