import React from "react";
import PropTypes from "prop-types";
import moment from "moment";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "react-datepicker/dist/react-datepicker-cssmodules.css";

import Portal from "../../../../Portal";
import ClickOffComponentWrapper from "../../../../ClickOffComponentWrapper";
import Dropdown from "../../../../FormComponents/Dropdown";
import getValueFromState from "../../../../utils/getValueFromState";

import { assignTestToStudentApi } from "../../../../Student/index/api";

const sampleVersions = [
  {
    label: "SAT Practice Test #1",
    value: "SAT Practice Test #1",
  },
  {
    label: "SAT Practice Test #2",
    value: "SAT Practice Test #2",
  },
  {
    label: "SAT Practice Test #3",
    value: "SAT Practice Test #3",
  },
];

const sampleSections = [
  {
    label: "Math (no calc)",
    value: "Math (no calc)",
  },
  {
    label: "Reading",
    value: "Reading",
  },
  {
    label: "Writing",
    value: "Writing",
  },
  {
    label: "Math (calculator)",
    value: "Math (calculator)",
  },
];

const uuidGenerator = require("uuid/v4");

class AssignTestSectionModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      version: "",
      section: "",
      assignDate: "",
      assignTime: "",
      dueDate: "",
      dueTime: "",
      timed: false,
    };
  }

  componentDidUpdate(prevProps, prevState) {
    const { modalDate } = this.props;
    if (prevState.assignDate === "" && modalDate) {
      // eslint-disable-next-line react/no-did-update-set-state
      this.setState({ assignDate: new Date(modalDate) });
    }
  }

  onResetModal = () =>
    this.setState({
      version: "",
      section: "",
      assignDate: "",
      assignTime: "",
      dueDate: "",
      dueTime: "",
      timed: false,
    });

  onCloseModal = () => {
    const { onClose } = this.props;
    onClose();
    this.onResetModal();
  };

  assignTestSection = () => {
    const { onAssignTestSection } = this.props;
    const {
      version,
      section,
      assignDate: unformattedAssignDate,
      assignTime: unformattedAssignTime,
      dueDate: unformattedDueDate,
      dueTime: unformattedDueTime,
      timed,
    } = this.state;
    const assignDate = moment(unformattedAssignDate).format("MM/DD/YY");
    const assignTime = moment(unformattedAssignTime).format("hh:mm");
    const dueDate = moment(unformattedDueDate).format("MM/DD/YY");
    const dueTime = moment(unformattedDueTime).format("hh:mm");
    onAssignTestSection({
      version,
      section,
      assignDate,
      assignTime,
      dueDate,
      dueTime,
      timed,
    });
    this.onResetModal();
    const {
      user: { id },
    } = this.props;
    const postBody = {
      student_id: id,
      test_id: uuidGenerator(),
      assignment_date: moment(unformattedAssignDate).format("YYYY-MM-DD"),
      due_date: moment(unformattedDueDate).format("YYYY-MM-DD"),
    };
    assignTestToStudentApi(postBody);
  };

  handleDetailsChange = (event, name = null) => {
    if (event.target) {
      this.setState(({ timed }) => ({ timed: !timed }));
    } else {
      this.setState({ [name]: event });
    }
  };

  handleDatePickerChange = (field, value) => this.setState({ [field]: value });

  render() {
    const { open } = this.props;
    const {
      version,
      section,
      assignDate,
      assignTime,
      dueDate,
      dueTime,
      timed,
    } = this.state;
    return (
      <Portal selector="#modal">
        {open && (
          <div className="overlay">
            <ClickOffComponentWrapper onOuterClick={this.onCloseModal}>
              <div
                id="modal_assign_test_section"
                className="modal modal-custom modal-460"
              >
                <div className="card-modal card">
                  <div
                    className="card-panel card-panel-title"
                    style={{ backgroundColor: "#00638e", color: "#fff" }}
                  >
                    <div className="card-panel-row row">
                      <div className="col">
                        <h2 className="h3">
                          <span className="heading-holder">
                            <i className="icon-assign-section"></i>
                            <span className="heading-block">
                              {" "}
                              Assign Test Section (as coursework)
                            </span>
                          </span>
                        </h2>
                      </div>
                      <div className="col right-align">
                        <a
                          href="#!"
                          onClick={this.onCloseModal}
                          className="panel-link close modal-close"
                        >
                          <i className="icon-close-thin"></i>
                        </a>
                      </div>
                    </div>
                  </div>
                  <div className="card-content">
