            unactivatedStudents: 29,
            averageImprovement: 185,
            averageInitialScore: 1037,
            averageFinalScore: 1218,
            studentsAchievingTargetScore: 12,
          },
        };
        finalArry.push(newInstructor);
        return finalArry;
      }, []);
      return formattedInstructors;
    })
    .catch(err => err);
};
export default [
  createNewInstructorApi,
