import React from 'react';
import Proptypes from 'prop-types';
import StudentCard from '../components/StudentCard';
import sampleStudentList from '../utils/sampleStudentList';
import FilterSection from './Components/FilterSection';
import StudentModal from "../components/StudentModal";

class StudentListPage extends React.Component {
    constructor (props){
        super(props);
        this.state = {
            students: sampleStudentList,
            studentModalOpen: false,
        }
    }

   onOpenStudentModal = () => this.setState({ studentModalOpen: true });
   onCloseStudentModal = () => this.setState({ studentModalOpen: false });     
   render() {
        const { students, studentModalOpen } = this.state;
        
        return(
            <React.Fragment>
                <StudentModal open={studentModalOpen} onClose={this.onCloseStudentModal} />
                <FilterSection />
                <div className="content-section">
                <div className="row d-flex-content">
                {students.map((student) => (
                    <StudentCard student={student} />
                    ))}
                    </div>
                    </div>
                    <a href="#" className="waves-effect waves-teal btn add-btn" onClick={this.onOpenStudentModal}><i className="material-icons">add</i>New Student</a>
            </React.Fragment>
        )
      }
    }

    StudentListPage.propTypes = {
        state: Proptypes.object.isRequired,
    }

export default StudentListPage;
