import React, { Component } from 'react';
import PropTypes from 'prop-types';

// eslint-disable-next-line react/prefer-stateless-function
class WorksheetListItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dropdownIsOpen: false,
    }
  }
  onSetDropdown = (event, dropdownIsOpen) => {
    console.log('clicked')
    event.preventDefault();
    this.setState({dropdownIsOpen: !dropdownIsOpen});
  };


  render() {
    const {dropdownIsOpen} = this.state;
    const {onDeleteWorksheet, index, worksheet: {worksheetName, problems,  timeEstimate, worksheetSource }} = this.props;
    return (
      <div className="card list-table-row">
      <div className="list-table-cell icon-cell">
        <span className="block-icon">
          <i className="icon-sheets-w"></i>
        </span>
      </div>
      <div className="list-table-cell name-cell">
        <div className="card-panel-text truncate">
          <div className="text-large truncate">{worksheetName}</div>
        </div>
      </div>
      <div className="list-table-cell description-cell">{problems}</div>
      <div className="list-table-cell time-cell">{timeEstimate}</div>
      <div className="list-table-cell completed-cell">{worksheetSource}</div>
      <div className="list-table-cell empty-cell"><b>&nbsp;</b></div>
      <div className="list-table-cell drop-cell">
        <div className="dropdown-block">
          <a
            className='dropdown-trigger btn'
            href='#'
            data-target='dropdown01'
            onClick={(event) => this.onSetDropdown(event, dropdownIsOpen)}>
            <i className="material-icons dots-icon">more_vert</i>
            </a>
          <If condition={dropdownIsOpen}>
          <ul id='dropdown01' className='dropdown-content dropdown-wide'style={{ display: "block", opacity: '1', transform: 'scaleX(1) scaleY(1)' }}>
            <li>
              {/* Modal Trigger */}
              <a href="#modal_user_edit" className="modal-trigger link-block">View Details</a>
            </li>
            <li><a href="#!">Dismiss Flags</a></li>
            <li><a href="#!">Reset</a></li>
            <li><a href="#!" className="link-delete" onClick={() => onDeleteWorksheet(index)}>Delete</a></li>
          </ul>
          </If>
        </div>
      </div>
    </div>
    );
  }
}

WorksheetListItem.propTypes = {
  worksheet: PropTypes.object.isRequired,
  onDeleteWorksheet: PropTypes.func.isRequired,
  index: PropTypes.number.isRequired,
}

export default WorksheetListItem;
