import React from 'react';
import PropTypes from 'prop-types';
import QuestionModal from '../QuestionModal';

const style = {
  red: {position: 'relative', color: '#32955c', borderColor: '#32955c', border: '1px solid', backgroundColor: '#fff', height: '19px', width: '19px', borderRadius: '50%'},
  greenFilled: {position: 'relative', color: '#fff', borderColor: '#32955c', border: '1px solid', backgroundColor: '#32955c', height: '19px', width: '19px', borderRadius: '50%'},
  greenBorderOnly: {position: 'relative', color: '#32955c', borderColor: '#32955c', border: '1px solid', backgroundColor: '#fff', height: '19px', width: '19px', borderRadius: '50%'},
  plain: {position: 'relative', color: '#a6a8ab', border: '1px solid', borderColor: '#a6a8ab', height: '19px', width: '19px', borderRadius: '50%'}
}

class AnswerRow extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      selectedAnswer: "bobby",
      correct: false,
     }
  };

  componentDidMount = () => {
    const { index, sampleAnswers } = this.props;
    const { studentChoice, answer } = sampleAnswers[index];
    this.setState({selectedAnswer: studentChoice});
    if(studentChoice === answer) {
      this.setState({correct: true})
    }
    // this.checkIfCorrect();
    // update counter on writing page
  };

  onOpenQuestionModal = () => this.setState({open: true})
  onCloseQuestionModal = () => this.setState({open: false});


  handleBubbleChange = (event) => {
    this.setState({
      selectedAnswer: event.target.value,
    })
    this.checkIfCorrect()
  }

  checkIfCorrect = () => {
    const { sampleAnswers, index } = this.props;
    const { selectedAnswer } = this.state;
    console.log('clicked', this.state.selectedAnswer)
    if(selectedAnswer === sampleAnswers[index].answer) {
      this.setState({correct: true});
    }
    // update counter on writing page
  }

  mapLetterBubbles = ({index}) => {
    const letters = ["A", "B", "C", "D"];
    const { sampleAnswers } = this.props;
    const { studentChoice, answer } = sampleAnswers[index];
    if (studentChoice === "") {
      return letters.map((letter) => {
        return (
          <li key={letter}>
            <form>
                <input
                  type="radio"
                  id={letter}
                  name={letter}
                  value={letter}
                  checked={this.state.selectedAnswer === letter}
                  onChange={this.handleBubbleChange}
                  />
                  <label
                  htmlFor={letter}
                  style={style.plain}>
                    <span style={{display: 'block', marginTop: '2px', paddingLeft: "4.5px"}}>{letter}</span>
              </label>
            </form>
          </li>
        )
      })
    }
    return letters.map(letter => {
      if (letter === answer && letter === studentChoice){
        return (
          <li key={letter}>
            <input
                type="radio"
                id={letter}
                name={letter}
                value={letter}
                checked={this.state.selectedAnswer === letter}
                onChange={this.handleBubbleChange}
                />
                <label
                htmlFor={letter}
                style={{position: 'relative', color: '#fff', borderColor: '#32955c', border: '1px solid', backgroundColor: '#32955c', height: '19px', width: '19px', borderRadius: '50%'}}>

                <span style={{marginTop: '2px', display: 'block', paddingLeft: "4.5px"}}>{letter}</span>
            </label>
        </li>
        )
      }
      if (letter === answer && letter !== studentChoice){
        return (
          <li key={letter}>
            <input
                type="radio"
                id={letter}
                name={letter}
                value={letter}
                checked={this.state.selectedAnswer === letter}
                onChange={this.handleBubbleChange}
                />
                <label
                htmlFor={letter}
                style={{position: 'relative', color: '#32955c', borderColor: '#32955c', border: '1px solid', backgroundColor: '#fff', height: '19px', width: '19px', borderRadius: '50%'}}>
                <span style={{marginTop: '2px', display: 'block', paddingLeft: "4.5px"}}>{letter}</span>
            </label>
          </li>
        )
      }
      if (letter !== answer && letter === studentChoice){
        return (
          <li key={letter}>
             <input
                type="radio"
                id={letter}
                name={letter}
                value={letter}
                checked={this.state.selectedAnswer === letter}
                onChange={this.handleBubbleChange}
                />
                <label
                htmlFor={letter}
                style={{position: 'relative', color: '#fff', borderColor: '#db1d41', border: '1px solid', backgroundColor: '#db1d41', height: '19px', width: '19px', borderRadius: '50%'}}>
                <span style={{marginTop: '2px', display: 'block', paddingLeft: "4.5px"}}>{letter}</span>
            </label>
          </li>
        )
      }
      return (
          <li key={letter}>
            <input
                type="radio"
                id={letter}
                name={letter}
                value={letter}
                checked={this.state.selectedAnswer === letter}
                onChange={this.handleBubbleChange}
                />
                <label
                htmlFor={letter}
                style={{position: 'relative', color: '#a6a8ab', border: '1px solid', borderColor: '#a6a8ab', height: '19px', width: '19px', borderRadius: '50%'}}>
                <span style={{display: 'block', marginTop: '2px', paddingLeft: "4.5px"}}>{letter}</span>
            </label>
          </li>
        )
      }
    )
  };

  mapNumberBubbles = ({ index} ) => {
    const { sampleAnswers } = this.props;
    const { answer, studentChoice }= sampleAnswers[index];
          if ( answer === studentChoice) {
        return (
          <li key={answer}>
            <span className="badge badge-rounded badge-rounded-bordered" style={{color: '#fff', borderColor: '#32955c', backgroundColor: '#3eb777'}}>{answer}</span>
          </li>
        )
      }
      return (
          <React.Fragment>
            <li key={studentChoice}>
              <span className="badge badge-rounded badge-rounded-bordered"  style={{color: '#fff', borderColor: '#ad1e3e', backgroundColor: '#db1d41'}}>{studentChoice}</span>
            </li>
            <li key={answer} >
              <span className="badge badge-rounded badge-rounded-bordered" style={{color: '#32955c', borderColor: '#32955c', backgroundColor: '#fff'}}>{answer}</span>
            </li>
          </React.Fragment>
        )
      };

      render() {
        const { index, question } = this.props;
        const { open } = this.state;
        return (
          <React.Fragment>
          <QuestionModal open={open} onOpenQuestionModal={this.onOpenQuestionModal} onCloseQuestionModal={this.onCloseQuestionModal} question={question}/>
        <li className="answers-list-holder" key={question.problem}>
          <div className="answer-row row mb-0" >
            <div className="col col-120">
              <ul className="answer-list opt_list truefalse">
                <Choose>
                  <When condition={!question.numeric}>
                  {this.mapLetterBubbles({index})}
                  </When>
                  <Otherwise>
                  {this.mapNumberBubbles({index})}
                  </Otherwise>
                </Choose>
                </ul>
            </div>
            <div className="col col-30">
              <span className="status-info">{question.status}</span>
            </div>
            <div className="col col-43">
              <span className="play-progress" data-video-id="video001">
                <span className="play"></span>
                <svg viewBox="0 0 50 50" width="50" height="50" version="1.1" xmlns="http://www.w3.org/2000/svg">
                  <circle className="circle-static" cx={25.8} cy={24.3} r={22.8} fill='transparent' strokeWidth={3}/>
                  <circle className="circle-progress" cx={25.8} cy={24.3} r={22.8} fill='transparent' strokeWidth={3}/>
                </svg>
              </span>
            </div>
            <div className="col col-auto">
              <If condition={question.flagged}>
                  <span className="status-answer" style={{color: '#c0272d'}}>
                    <i className="icon-flag"></i><b className="status-text">Review</b>
                  </span>
              </If>
            </div>
            <div className="dropdown-block col col-35">

              <a className='modal-trigger' href="#" onClick={this.onOpenQuestionModal}><i className="material-icons dots-icon">more_vert</i></a>
            </div>
          </div>
          <If condition={question.studentNotes}>
            <div className="comment-block">
              <p>{question.studentNotes}</p>
            </div>
          </If>
        </li>
        <style jsx>
          {`
            input[type="radio"]:checked {
              position: relative;
              opacity: 1;
            }
            input {
              margin: 2px,
            }
          `}
          </style>
      </React.Fragment>

    )
  }
}

AnswerRow.propTypes = {
  question: PropTypes.object.isRequired,
  index: PropTypes.number.isRequired,
  sampleAnswers: PropTypes.array.isRequired,
}

export default AnswerRow;
