import React, { Component } from 'react';
import PropTypes from 'prop-types';
import update from 'immutability-helper';
import Dropdown from '../../../FormComponents/Dropdown';
import getValueFromState from '../../../utils/getValueFromState';
import sampleTopics from '../../../utils/sampleTopics';
import worksheetOptions from '../../utils/worksheetOptions';

// eslint-disable-next-line react/prefer-stateless-function
class FilterSection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      worksheetName: '',
      topic: {},
      sort: {},
      activeFilters: [],
    };
  }

  onToggleShowFilters = () => this.setState(({ open }) => ({ open: !open }))
  onClearFilters = () => this.setState({ activeFilters: [] })

  handleSortChange = (sort) => {
    const {onSetSort} = this.props;
    this.setState({sort});
    return onSetSort(sort);
  }
  handleFilterClick = (filter) => {
    const { activeFilters: currentActiveFilters } = this.state;
    let activeFilters;
    if (currentActiveFilters.indexOf(filter) === -1) {
      activeFilters = update(currentActiveFilters, {
        $push: [filter],
      });
    } else {
      const filterIndex = currentActiveFilters.indexOf(filter);
      activeFilters = update(currentActiveFilters, {
        $splice: [[ filterIndex, 1 ]],
      });
    }
    this.setState({ activeFilters });
  }

  handleFilterChange = (event, name) => {
    const { onSetFilteredTopicState, onUnsetFilteredTopicState, onSetSort } = this.props;
    const value = event.target ? event.target.value : event;
    const updatedState = update(this.state, {
      $merge: { [name]: value },
    });
    this.setState(updatedState);
    if (name === 'topic') {
      if (event === '') {
        return onUnsetFilteredTopicState();
      }
      return onSetFilteredTopicState(event);
    } else if (name === 'sort') {
      return onSetSort(event);
    }
  }

  submitNameFilter = () => {
    const { onSetFilteredState, onUnsetFilteredState } = this.props;
    const { worksheetName } = this.state;
    if (worksheetName === '') {
      onUnsetFilteredState();
    }
    const transformedName = worksheetName.replace(/\s/g, "").toLowerCase();
    onSetFilteredState(transformedName);
  }



  render() {
    const { open, activeFilters, worksheetName, topic, sort } = this.state;
    const { currentView, onChangeView } = this.props;

    return (
      <div className="filter-form-holder">
      <ul className="collapsible expandable">
        <li>
          <div className="collapsible-body" style={open ? { display: "block"} : {display: 'none'}}>
            <div className="filter-form_checkbox-list-holder justify-center">
              <ul className="filter-form_checkbox-list">
                <li>
                  <input
                    type="checkbox"
                    id="reading"
                    checked={activeFilters.indexOf('reading') !== -1}
                    onChange={() => this.handleFilterClick('reading')}
                  />
                  <label htmlFor="reading">Reading</label>
                </li>
                <li>
                    <input
                      type="checkbox"
                      id="writing"
                      checked={activeFilters.indexOf('writing') !== -1}
                      onChange={() => this.handleFilterClick('writing')}
                    />
                    <label htmlFor="writing">Writing</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="math"
                      checked={activeFilters.indexOf('math') !== -1}
                      onChange={() => this.handleFilterClick('math')}
                    />
                    <label htmlFor="math">Math</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="mixed"
                      checked={activeFilters.indexOf('mixed') !== -1}
                      onChange={() => this.handleFilterClick('mixed')}
                    />
                    <label htmlFor="mixed">Mixed</label>
                  </li>
              </ul>
              <ul className="filter-form_checkbox-list">
              <li>
                    <input
                      type="checkbox"
                      id="basic"
                      checked={activeFilters.indexOf('basic') !== -1}
                      onChange={() => this.handleFilterClick('basic')}
                    />
                    <label htmlFor="basic">Basic</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="medium"
                      checked={activeFilters.indexOf('medium') !== -1}
                      onChange={() => this.handleFilterClick('medium')}
                    />
                    <label htmlFor="medium">Medium</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="advanced"
                      checked={activeFilters.indexOf('advanced') !== -1}
                      onChange={() => this.handleFilterClick('advanced')}
                    />
                    <label htmlFor="advanced">Advanced</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="mixed"
                      checked={activeFilters.indexOf('mixed') !== -1}
                      onChange={() => this.handleFilterClick('mixed')}
                    />
                    <label htmlFor="mixed">Mixed</label>
                  </li>
              </ul>
              <ul className="filter-form_checkbox-list">
              <li>
                    <input
                      type="checkbox"
                      id="skill-builder"
                      checked={activeFilters.indexOf('skill-builder') !== -1}
                      onChange={() => this.handleFilterClick('skill-builder')}
                    />
                    <label htmlFor="skill-builder">Skill Builder</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="sat-practice"
                      checked={activeFilters.indexOf('sat-practice') !== -1}
                      onChange={() => this.handleFilterClick('sat-practice')}
                    />
                    <label htmlFor="sat-practice">SAT Practice</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="mixed"
                      checked={activeFilters.indexOf('mixed') !== -1}
                      onChange={() => this.handleFilterClick('mixed')}
                    />
                    <label htmlFor="mixed">Mixed</label>
                  </li>
              </ul>
              <ul className="filter-form_checkbox-list">
              <li>
                    <input
                      type="checkbox"
                      id="built-in"
                      checked={activeFilters.indexOf('built-in') !== -1}
                      onChange={() => this.handleFilterClick('built-in')}
                    />
                    <label htmlFor="built-in">Built-in</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="user-created"
                      checked={activeFilters.indexOf('user-created') !== -1}
                      onChange={() => this.handleFilterClick('user-created')}
                    />
                    <label htmlFor="user-created">User Created</label>
                  </li>
              </ul>
            </div>
            <div className="d-flex row mb-0 justify-center">
              <div className="col s12 m3" style={{marginTop: '36px'}}>
                <div className="search-field input-field">
                  <input type="search" id="name_search" className="input-control  validate" placeholder="Speci" />
                  <button type="submit" className="search-button"><i className="icon-search"></i></button>
                  <label className="label" htmlFor="name_search">Search</label>
                </div>
              </div>
              <div className="col s12 m3">
                <div className="input-field">
                <Dropdown
                      value={getValueFromState(topic, sampleTopics)}
                      onChange={(event) => this.handleFilterChange(event, 'topic')}
                      options={sampleTopics}
                      label="Topic"
                      stateKey="topic"
                      dropdownKey="topic"
                    />
                </div>
              </div>
            </div>
          </div>
          <div className="row mb-0 d-flex align-items-center">
            <div className="col s12 l4">
              <div className="row mb-0">
                <div className="col s12 xl7">
                  <div className="input-field">
                    <Dropdown
                      value={getValueFromState(sort, worksheetOptions)}
                      onChange={this.handleSortChange}
                      options={worksheetOptions}
                      label="Sort by"
                      stateKey="sort"
                      dropdownKey="sort"
                    />
                    {/* <select id="sort_worksheet">
                      <option>Worksheet Name</option>
                      <option>Worksheet Name</option>
                      <option>Worksheet Name</option>
                    </select>
                    <label className="label" htmlFor="sort_worksheet">Sort By</label> */}
                  </div>
                </div>
              </div>
            </div>
            <div className="switcher-block col s12 l4" style={{marginTop: '14px'}}>
              <div className="view-switcher">
                <ul className="switcher center-align">
                  <li
                  data-view="view-full"
                  className={currentView === 'full' ? 'active' : ""}
                  >
                    <a href="#" onClick={() => onChangeView('full')}>Full View</a>
                  </li>
                  <li
                      data-view="view-list"
                      className={currentView === 'list' ? 'active' : ''}
                    >
                      <a href="#" onClick={() => onChangeView('list')}>List View</a>
                    </li>
                  </ul>
              </div>
            </div>
            <div className="col s12 l4">
              <div className="option-filters">
              <div className="option-item clear"><a href="#" onClick={this.onClearFilters}>Clear Filters</a></div>
                <a role="button" tabIndex="0" onClick={this.onToggleShowFilters}>
                  <div className="option-item">
                    <span
                      className="collapsible-header"
                    >
                      <span className="open-text">{open ? 'Hide Filters' : 'Show Filters'}</span>
                    </span>
                  </div>
                    </a>
              </div>
            </div>
          </div>
        </li>
      </ul>
    </div>
    );
  }
}

FilterSection.propTypes = {
  currentView: PropTypes.string.isRequired,
  onChangeView: PropTypes.func.isRequired,
  onSetSort: PropTypes.func.isRequired,
  onSetFilteredTopicState: PropTypes.func.isRequired,
  onUnsetFilteredTopicState: PropTypes.func.isRequired,
  onSetFilteredState: PropTypes.func.isRequired,
  onUnsetFilteredState: PropTypes.func.isRequired,
}

export default FilterSection;
