import React, { Component } from 'react';
import PropTypes from 'prop-types';
import update from 'immutability-helper';
import Dropdown from '../../../FormComponents/Dropdown';
import getValueFromState from '../../../utils/getValueFromState';
import sampleTopics from '../../../utils/sampleTopics';
import worksheetOptions from '../../utils/worksheetOptions';

// eslint-disable-next-line react/prefer-stateless-function
class FilterSection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      filterName: '',
      topic: {},
      sort: {},
    };
  }

  onToggleShowFilters = () => this.setState(({ open }) => ({ open: !open }))
  handleTopicChange = (topic) => {
    const { onSetFilteredTopicState } = this.props;
    this.setState({topic});
    return onSetFilteredTopicState(topic)
  }
  handleSortChange = (sort) => {
    const {onSetSort} = this.props;
    this.setState({sort});
    return onSetSort(sort);
  }
  
  submitNameFilter = () => {
    const { onSetFilteredState, onUnsetFilteredState } = this.props;
    const { filterName } = this.state;
    if (filterName === '') {
      onUnsetFilteredState(filterName);
    }
    const transformedName = filterName.replace(/\s/g, "").toLowerCase();
    onSetFilteredState(transformedName);
  }

  handleFilterChange = (event, name) => {
    const { onUnsetFilteredState, onSetFilteredState} = this.props;
    const value = event.target ? event.target.value : event;
    const updatedState = update(this.state, {
      $merge: { [name]: value },
    });
    this.setState(updatedState);
    if (name === 'filterName') {
      if (event === '') {
        return onUnsetFilteredState();
      }
      return onSetFilteredState(event);
    }
    if (name === 'filterTopic') {
      if (event === "") {
        return onUnsetFilteredState();
      }
      return onSetFilteredState(event);
    } 
  }


  render() {
    const { open,  filterName, topic, sort } = this.state;
    const { currentView, onChangeView, onClearFilters, subjectFilters, handleFilterClick, difficultyFilters, sourceFilters, problemTypeFilters } = this.props;

    return (
      <div className="filter-form-holder">
      <ul className="collapsible expandable">
        <li>
          <div className="collapsible-body" style={open ? { display: "block"} : {display: 'none'}}>
            <div className="filter-form_checkbox-list-holder justify-center">
              <ul className="filter-form_checkbox-list">
                <li>
                  <input
                    type="checkbox"
                    id="reading"
                    checked={subjectFilters.indexOf('Reading') !== -1}
                    onChange={() => handleFilterClick('subject', 'Reading')}
                  />
                  <label htmlFor="reading">Reading</label>
                </li>
                <li>
                    <input
                      type="checkbox"
                      id="writing"
                      checked={subjectFilters.indexOf('Writing') !== -1}
                      onChange={() => handleFilterClick('subject', 'Writing')}
                    />
                    <label htmlFor="writing">Writing</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="math"
                      checked={subjectFilters.indexOf('Math') !== -1}
                      onChange={() => handleFilterClick('subject', 'Math')}
                    />
                    <label htmlFor="math">Math</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="mixed"
                      checked={subjectFilters.indexOf('Mixed') !== -1}
                      onChange={() => handleFilterClick('subject', 'Mixed')}
                    />
                    <label htmlFor="mixed">Mixed</label>
                  </li>
              </ul>
              <ul className="filter-form_checkbox-list">
              <li>
                    <input
                      type="checkbox"
                      id="basic"
                      // checked={difficultyFilters.indexOf('basic') !== -1}
                      onChange={() => handleFilterClick('difficulty', 'Basic')}
                    />
                    <label htmlFor="basic">Basic</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="medium"
                      // checked={difficultyFilters.indexOf('medium') !== -1}
                      onChange={() => handleFilterClick('difficulty', 'Medium')}
                    />
                    <label htmlFor="medium">Medium</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="advanced"
                      // checked={difficultyFilters.indexOf('advanced') !== -1}
                      onChange={() => handleFilterClick('difficulty', 'Advanced')}
                    />
                    <label htmlFor="advanced">Advanced</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="mixed"
                      // checked={difficultyFilters.indexOf('mixed') !== -1}
                      onChange={() => handleFilterClick('difficulty', 'Mixed')}
                    />
                    <label htmlFor="mixed">Mixed</label>
                  </li>
              </ul>
              <ul className="filter-form_checkbox-list">
              <li>
                    <input
                      type="checkbox"
                      id="skill-builder"
                      checked={problemTypeFilters.indexOf('Skill Builder') !== -1}
                      onChange={() => handleFilterClick('problemType', 'Skill Builder')}
                    />
                    <label htmlFor="skill-builder">Skill Builder</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="sat-practice"
                      checked={problemTypeFilters.indexOf('sat-practice') !== -1}
                      onChange={() => handleFilterClick('problemType', 'sat-practice')}
                    />
                    <label htmlFor="sat-practice">SAT Practice</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="mixed"
                      checked={problemTypeFilters.indexOf('Mixed') !== -1}
                      onChange={() => handleFilterClick('problemType', 'Mixed')}
                    />
                    <label htmlFor="mixed">Mixed</label>
                  </li>
              </ul>
              <ul className="filter-form_checkbox-list">
              <li>
                    <input
                      type="checkbox"
                      id="built-in"
                      checked={sourceFilters.indexOf('built-in') !== -1}
                      onChange={() => handleFilterClick('source', 'built-in')}
                    />
                    <label htmlFor="built-in">Built-in</label>
                  </li>
                  <li>
                    <input
                      type="checkbox"
                      id="user-created"
                      checked={sourceFilters.indexOf('User Created') !== -1}
                      onChange={() => handleFilterClick('source', 'User Created')}
                    />
                    <label htmlFor="user-created">User Created</label>
                  </li>
              </ul>
            </div>
            <div className="d-flex row mb-0 justify-center">
              <div className="col s12 m3" style={{marginTop: '36px'}}>
                <div className="search-field input-field">
                  <input
                  type="search"
                  id="name_search"
                  className="input-control  validate"
                  placeholder="Speci" 
                  name="filterName"
                  value={filterName}
                  onChange={(event) => this.handleFilterChange(event, 'filterName')}/>
                  <button
                    type="submit"
                    className="search-button"
                    onClick={this.submitNameFilter}>
                    <i className="icon-search"></i></button>
                  <label className={filterName.length ? "label active" : "label"} htmlFor="name_search">Search</label>
                </div>
              </div>
              <div className="col s12 m3">
                <div className="input-field">
                <Dropdown
                      value={getValueFromState(topic, sampleTopics)}
                      onChange={this.handleTopicChange}
                      options={sampleTopics}
                      label="Topic"
                      stateKey="topic"
                      dropdownKey="topic"
                    />
                </div>
              </div>
            </div>
          </div>
          <div className="row mb-0 d-flex align-items-center">
            <div className="col s12 l4">
              <div className="row mb-0">
                <div className="col s12 xl7">
                  <div className="input-field">
                    <Dropdown
                      value={getValueFromState(sort, worksheetOptions)}
                      onChange={(event) => this.handleFilterChange(event, "sort")}
                      options={worksheetOptions}
                      label="Sort by"
                      stateKey="sort"
                      dropdownKey="sort"
                    />
                    {/* <select id="sort_worksheet">
                      <option>Worksheet Name</option>
                      <option>Worksheet Name</option>
                      <option>Worksheet Name</option>
                    </select>
                    <label className="label" htmlFor="sort_worksheet">Sort By</label> */}
                  </div>
                </div>
              </div>
            </div>
            <div className="switcher-block col s12 l4" style={{marginTop: '14px'}}>
              <div className="view-switcher">
                <ul className="switcher center-align">
                  <li
                  data-view="view-full"
                  className={currentView === 'full' ? 'active' : ""}
                  >
                    <a href="#" onClick={() => onChangeView('full')}>Full View</a>
                  </li>
                  <li
                      data-view="view-list"
                      className={currentView === 'list' ? 'active' : ''}
                    >
                      <a href="#" onClick={() => onChangeView('list')}>List View</a>
                    </li>
                  </ul>
              </div>
            </div>
            <div className="col s12 l4">
              <div className="option-filters">
              <div className="option-item clear"><a href="#" onClick={() => onClearFilters()}>Clear Filters</a></div>
                <a role="button" tabIndex="0" onClick={this.onToggleShowFilters}>
                  <div className="option-item">
                    <span
                      className="collapsible-header"
                    >
                      <span className="open-text">{open ? 'Hide Filters' : 'Show Filters'}</span>
                    </span>
                  </div>
                    </a>
              </div>
            </div>
          </div>
        </li>
      </ul>
    </div>
    );
  }
}

FilterSection.propTypes = {
  currentView: PropTypes.string.isRequired,
  onChangeView: PropTypes.func.isRequired,
  onSetSort: PropTypes.func.isRequired,
  onSetFilteredTopicState: PropTypes.func.isRequired,
  onSetFilteredState: PropTypes.func.isRequired,
  onUnsetFilteredState: PropTypes.func.isRequired,
  subjectFilters: PropTypes.array.isRequired,
  difficultyFilters: PropTypes.array.isRequired,
  problemTypeFilters: PropTypes.array.isRequired,
  sourceFilters: PropTypes.array.isRequired,
  onClearFilters: PropTypes.func.isRequired,
  handleFilterClick: PropTypes.func.isRequired,
}

export default FilterSection;
