/* eslint-disable react/no-array-index-key */
import React from 'react';
import PropTypes from 'prop-types';
import update from 'immutability-helper';

import LessonCard from './components/LessonCard';
import FilterSection from './components/FilterSection';

class DetailLessonList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      test: true,
      originalUser: {
        lessons: "",
      },
      updatedUser: {
        lessons: "",
      },
    }
  }

  componentDidMount() {
    const { user: { lessons } = {} } = this.props;
    const updatedUser = { lessons };
    const { originalUser: originalUserState } = this.state;
    const originalUser = update(originalUserState, {
      $merge: updatedUser,
    });
    this.setState({ originalUser, updatedUser }, console.log(this.state)); // eslint-disable-line
  }

  onRemoveOption = (optionIndex, array) => {
    const updatedUser = update(this.state.updatedUser, {
        [array]: {
          $splice: [[optionIndex, 1]],
        },
    });
    this.setState({ updatedUser });
  }

  // onRemoveLesson= (optionIndex) => {
  //   console.log(this.state.updatedLessons)
  //   const updatedLessons = update(this.state.updatedLessons, {
  //     updatedLessons: {
  //       $splice: [[optionIndex, 1]],
  //     },
  //   });
  //   this.setState({updatedLessons})
  // }
  // onRemoveLesson= (optionIndex) => {
  //   const originalLessons = update(this.state.originalLessons, {
  //     originalLessons: {
  //       $splice: [[optionIndex, 1]],
  //     },
  //   });
  //   this.setState({originalLessons})
  // }

  mapLessons = () => this.state.originalUser.lessons.map((lesson, index) => (
    <LessonCard
      key={index} index={index} lesson={lesson} onRemoveLesson={() => this.onRemoveLesson(index, 'lessons')}
    />
  ))

  render() {
    return (
      <React.Fragment>
        <FilterSection />
        <div className="content-section">
          <div className="row d-flex-content card-width-272">
            {this.mapLessons()}
          </div>
        </div>
      <a href="#" className="waves-effect waves-teal btn add-btn"><i className="material-icons">add</i>New Lesson</a>
      </React.Fragment>
    )
  }
}

DetailLessonList.propTypes = {
  user: PropTypes.object.isRequired,
};

export default DetailLessonList;
